example_queries.products:
query GetProducts($first: Int = 10) {
  products(first: $first) {
    edges {
      node {
        id
        name
        price
      }
    }
    pageInfo {
      hasNextPage
      endCursor
    }
  }
}

example_queries.create_product:
mutation CreateProduct($input: CreateProductInput!) {
  createProduct(input: $input) {
    product {
      id
      name
      price
    }
    errors {
      field
      message
    }
  }
}

example_queries.subscribe_orders:
subscription OnOrderCreated {
  orderCreated {
    id
    totalAmount
    customer {
      name
      email
    }
  }
}
